	struct elemental_vector
	{
		void* m_p;
		uint32 m_size;
		uint32 m_capacity;

		typedef void(*object_mover)(void* pDst, void* pSrc, uint32 num);

		bool increase_capacity(uint32 min_new_capacity, bool grow_hint, uint32 element_size, object_mover pRelocate);
	};

