// Handy MACRO.
#define SWITCH_ID_LIST(INDEX, LIST)                                           \
  if (idx == (INDEX)) {                                                       \
	const WebPChunk* const chunk = ChunkSearchList((LIST), nth,               \
												   kChunks[(INDEX)].tag);     \
	if (chunk) {                                                              \
	  *data = chunk->data_;                                                   \
	  return WEBP_MUX_OK;                                                     \
	} else {                                                                  \
	  return WEBP_MUX_NOT_FOUND;                                              \
	}                                                                         \
  }

static WebPMuxError MuxGet(const WebPMux* const mux, CHUNK_INDEX idx,
						   uint32_t nth, WebPData* const data) {
  assert(mux != NULL);
  assert(!IsWPI(kChunks[idx].id));
  WebPDataInit(data);

  SWITCH_ID_LIST(IDX_VP8X, mux->vp8x_);
  SWITCH_ID_LIST(IDX_ICCP, mux->iccp_);
  SWITCH_ID_LIST(IDX_ANIM, mux->anim_);
  SWITCH_ID_LIST(IDX_EXIF, mux->exif_);
  SWITCH_ID_LIST(IDX_XMP, mux->xmp_);
  SWITCH_ID_LIST(IDX_UNKNOWN, mux->unknown_);
  return WEBP_MUX_NOT_FOUND;
}
#undef SWITCH_ID_LIST

// Fill the chunk with the given data (includes chunk header bytes), after some
// verifications.
static WebPMuxError ChunkVerifyAndAssign(WebPChunk* chunk,
										 const uint8_t* data, size_t data_size,
										 size_t riff_size, int copy_data) {
  uint32_t chunk_size;
  WebPData chunk_data;

  // Sanity checks.
  if (data_size < TAG_SIZE) return WEBP_MUX_NOT_ENOUGH_DATA;
  chunk_size = GetLE32(data + TAG_SIZE);

  {
	const size_t chunk_disk_size = SizeWithPadding(chunk_size);
	if (chunk_disk_size > riff_size) return WEBP_MUX_BAD_DATA;
	if (chunk_disk_size > data_size) return WEBP_MUX_NOT_ENOUGH_DATA;
  }

  // Data assignment.
  chunk_data.bytes = data + CHUNK_HEADER_SIZE;
  chunk_data.size = chunk_size;
  return ChunkAssignData(chunk, &chunk_data, copy_data, GetLE32(data + 0));
}

int MuxImageFinalize(WebPMuxImage* const wpi) {
  const WebPChunk* const img = wpi->img_;
  assert(img != NULL);
  const WebPData* const image = &img->data_;
  const int is_lossless = (img->tag_ == kChunks[IDX_VP8L].tag);
  int w, h;
  int vp8l_has_alpha = 0;
  const int ok = is_lossless ?
	  VP8LGetInfo(image->bytes, image->size, &w, &h, &vp8l_has_alpha) :
	  VP8GetInfo(image->bytes, image->size, image->size, &w, &h);
   if (ok) {
	// Ignore ALPH chunk accompanying VP8L.
	if (is_lossless && (wpi->alpha_ != NULL)) {
	  ChunkDelete(wpi->alpha_);
	  wpi->alpha_ = NULL;
	}
	wpi->width_ = w;
	wpi->height_ = h;
	wpi->has_alpha_ = vp8l_has_alpha || (wpi->alpha_ != NULL);
  }
  return ok;
}

static int MuxImageParse(const WebPChunk* const chunk, int copy_data,
						 WebPMuxImage* const wpi) {
  const uint8_t* bytes = chunk->data_.bytes;
  size_t size = chunk->data_.size;
  const uint8_t* const last = bytes + size;
  WebPChunk subchunk;
  size_t subchunk_size;
  ChunkInit(&subchunk);

  assert(chunk->tag_ == kChunks[IDX_ANMF].tag ||
		 chunk->tag_ == kChunks[IDX_FRGM].tag);
  assert(!wpi->is_partial_);

  // ANMF/FRGM.
  {
	const size_t hdr_size = (chunk->tag_ == kChunks[IDX_ANMF].tag) ?
		ANMF_CHUNK_SIZE : FRGM_CHUNK_SIZE;
	const WebPData temp = { bytes, hdr_size };
	// Each of ANMF and FRGM chunk contain a header at the beginning. So, its
	// size should at least be 'hdr_size'.
	if (size < hdr_size) goto Fail;
	ChunkAssignData(&subchunk, &temp, copy_data, chunk->tag_);
  }
  ChunkSetNth(&subchunk, &wpi->header_, 1);
  wpi->is_partial_ = 1;  // Waiting for ALPH and/or VP8/VP8L chunks.

  // Rest of the chunks.
  subchunk_size = ChunkDiskSize(&subchunk) - CHUNK_HEADER_SIZE;
  bytes += subchunk_size;
  size -= subchunk_size;

  while (bytes != last) {
	ChunkInit(&subchunk);
	if (ChunkVerifyAndAssign(&subchunk, bytes, size, size,
							 copy_data) != WEBP_MUX_OK) {
	  goto Fail;
	}
	switch (ChunkGetIdFromTag(subchunk.tag_)) {
	  case WEBP_CHUNK_ALPHA:
		if (wpi->alpha_ != NULL) goto Fail;  // Consecutive ALPH chunks.
		if (ChunkSetNth(&subchunk, &wpi->alpha_, 1) != WEBP_MUX_OK) goto Fail;
		wpi->is_partial_ = 1;  // Waiting for a VP8 chunk.
		break;
	  case WEBP_CHUNK_IMAGE:
		if (ChunkSetNth(&subchunk, &wpi->img_, 1) != WEBP_MUX_OK) goto Fail;
		if (!MuxImageFinalize(wpi)) goto Fail;
		wpi->is_partial_ = 0;  // wpi is completely filled.
		break;
	  case WEBP_CHUNK_UNKNOWN:
		if (wpi->is_partial_) goto Fail;  // Encountered an unknown chunk
										  // before some image chunks.
		if (ChunkSetNth(&subchunk, &wpi->unknown_, 0) != WEBP_MUX_OK) goto Fail;
		break;
	  default:
		goto Fail;
		break;
	}
	subchunk_size = ChunkDiskSize(&subchunk);
	bytes += subchunk_size;
	size -= subchunk_size;
  }
  if (wpi->is_partial_) goto Fail;
  return 1;

 Fail:
  ChunkRelease(&subchunk);
  return 0;
}

//------------------------------------------------------------------------------
// Create a mux object from WebP-RIFF data.

WebPMux* WebPMuxCreateInternal(const WebPData* bitstream, int copy_data,
							   int version) {
  size_t riff_size;
  uint32_t tag;
  const uint8_t* end;
  WebPMux* mux = NULL;
  WebPMuxImage* wpi = NULL;
  const uint8_t* data;
  size_t size;
  WebPChunk chunk;
  ChunkInit(&chunk);

  // Sanity checks.
  if (WEBP_ABI_IS_INCOMPATIBLE(version, WEBP_MUX_ABI_VERSION)) {
	return NULL;  // version mismatch
  }
  if (bitstream == NULL) return NULL;

  data = bitstream->bytes;
  size = bitstream->size;

  if (data == NULL) return NULL;
  if (size < RIFF_HEADER_SIZE) return NULL;
  if (GetLE32(data + 0) != MKFOURCC('R', 'I', 'F', 'F') ||
	  GetLE32(data + CHUNK_HEADER_SIZE) != MKFOURCC('W', 'E', 'B', 'P')) {
	return NULL;
  }

  mux = WebPMuxNew();
  if (mux == NULL) return NULL;

  if (size < RIFF_HEADER_SIZE + TAG_SIZE) goto Err;

  tag = GetLE32(data + RIFF_HEADER_SIZE);
  if (tag != kChunks[IDX_VP8].tag &&
	  tag != kChunks[IDX_VP8L].tag &&
	  tag != kChunks[IDX_VP8X].tag) {
	goto Err;  // First chunk should be VP8, VP8L or VP8X.
  }

  riff_size = SizeWithPadding(GetLE32(data + TAG_SIZE));
  if (riff_size > MAX_CHUNK_PAYLOAD || riff_size > size) {
	goto Err;
  } else {
	if (riff_size < size) {  // Redundant data after last chunk.
	  size = riff_size;  // To make sure we don't read any data beyond mux_size.
	}
  }

  end = data + size;
  data += RIFF_HEADER_SIZE;
  size -= RIFF_HEADER_SIZE;

  wpi = (WebPMuxImage*)malloc(sizeof(*wpi));
  if (wpi == NULL) goto Err;
  MuxImageInit(wpi);

  // Loop over chunks.
  while (data != end) {
	size_t data_size;
	WebPChunkId id;
	WebPChunk** chunk_list;
	if (ChunkVerifyAndAssign(&chunk, data, size, riff_size,
							 copy_data) != WEBP_MUX_OK) {
	  goto Err;
	}
	data_size = ChunkDiskSize(&chunk);
	id = ChunkGetIdFromTag(chunk.tag_);
	switch (id) {
	  case WEBP_CHUNK_ALPHA:
		if (wpi->alpha_ != NULL) goto Err;  // Consecutive ALPH chunks.
		if (ChunkSetNth(&chunk, &wpi->alpha_, 1) != WEBP_MUX_OK) goto Err;
		wpi->is_partial_ = 1;  // Waiting for a VP8 chunk.
		break;
	  case WEBP_CHUNK_IMAGE:
		if (ChunkSetNth(&chunk, &wpi->img_, 1) != WEBP_MUX_OK) goto Err;
		if (!MuxImageFinalize(wpi)) goto Err;
		wpi->is_partial_ = 0;  // wpi is completely filled.
 PushImage:
		// Add this to mux->images_ list.
		if (MuxImagePush(wpi, &mux->images_) != WEBP_MUX_OK) goto Err;
		MuxImageInit(wpi);  // Reset for reading next image.
		break;
	  case WEBP_CHUNK_ANMF:
#ifdef WEBP_EXPERIMENTAL_FEATURES
	  case WEBP_CHUNK_FRGM:
#endif
		if (wpi->is_partial_) goto Err;  // Previous wpi is still incomplete.
		if (!MuxImageParse(&chunk, copy_data, wpi)) goto Err;
		ChunkRelease(&chunk);
		goto PushImage;
		break;
	  default:  // A non-image chunk.
		if (wpi->is_partial_) goto Err;  // Encountered a non-image chunk before
										 // getting all chunks of an image.
		chunk_list = MuxGetChunkListFromId(mux, id);  // List to add this chunk.
		if (ChunkSetNth(&chunk, chunk_list, 0) != WEBP_MUX_OK) goto Err;
		break;
	}
	data += data_size;
	size -= data_size;
	ChunkInit(&chunk);
  }

  // Validate mux if complete.
  if (MuxValidate(mux) != WEBP_MUX_OK) goto Err;

  MuxImageDelete(wpi);
  return mux;  // All OK;

 Err:  // Something bad happened.
  ChunkRelease(&chunk);
  MuxImageDelete(wpi);
  WebPMuxDelete(mux);
  return NULL;
}

