static int TreeAddSymbol(HuffmanTree* const tree,
						 int symbol, int code, int code_length) {
  int step = HUFF_LUT_BITS;
  int base_code;
  HuffmanTreeNode* node = tree->root_;
  const HuffmanTreeNode* const max_node = tree->root_ + tree->max_nodes_;
  assert(symbol == (int16_t)symbol);
  if (code_length <= HUFF_LUT_BITS) {
	int i;
	base_code = ReverseBitsShort(code, code_length);
	for (i = 0; i < (1 << (HUFF_LUT_BITS - code_length)); ++i) {
	  const int idx = base_code | (i << code_length);
	  tree->lut_symbol_[idx] = (int16_t)symbol;
	  tree->lut_bits_[idx] = code_length;
	}
  } else {
	base_code = ReverseBitsShort((code >> (code_length - HUFF_LUT_BITS)),
								 HUFF_LUT_BITS);
  }
  while (code_length-- > 0) {
	if (node >= max_node) {
	  return 0;
	}
	if (NodeIsEmpty(node)) {
	  if (IsFull(tree)) return 0;    // error: too many symbols.
	  AssignChildren(tree, node);
	} else if (!HuffmanTreeNodeIsNotLeaf(node)) {
	  return 0;  // leaf is already occupied.
	}
	node += node->children_ + ((code >> code_length) & 1);
	if (--step == 0) {
	  tree->lut_jump_[base_code] = (int16_t)(node - tree->root_);
	}
  }
  if (NodeIsEmpty(node)) {
	node->children_ = 0;      // turn newly created node into a leaf.
  } else if (HuffmanTreeNodeIsNotLeaf(node)) {
	return 0;   // trying to assign a symbol to already used code.
  }
  node->symbol_ = symbol;  // Add symbol in this node.
  return 1;
}

int HuffmanTreeBuildImplicit(HuffmanTree* const tree,
							 const int* const code_lengths,
							 int code_lengths_size) {
  int symbol;
  int num_symbols = 0;
  int root_symbol = 0;

  assert(tree != NULL);
  assert(code_lengths != NULL);

  // Find out number of symbols and the root symbol.
  for (symbol = 0; symbol < code_lengths_size; ++symbol) {
	if (code_lengths[symbol] > 0) {
	  // Note: code length = 0 indicates non-existent symbol.
	  ++num_symbols;
	  root_symbol = symbol;
	}
  }

  // Initialize the tree. Will fail for num_symbols = 0
  if (!TreeInit(tree, num_symbols)) return 0;

  // Build tree.
  if (num_symbols == 1) {  // Trivial case.
	const int max_symbol = code_lengths_size;
	if (root_symbol < 0 || root_symbol >= max_symbol) {
	  HuffmanTreeRelease(tree);
	  return 0;
	}
	return TreeAddSymbol(tree, root_symbol, 0, 0);
  } else {  // Normal case.
	int ok = 0;

	// Get Huffman codes from the code lengths.
	int* const codes =
		(int*)WebPSafeMalloc((uint64_t)code_lengths_size, sizeof(*codes));
	if (codes == NULL) goto End;

	if (!HuffmanCodeLengthsToCodes(code_lengths, code_lengths_size, codes)) {
	  goto End;
	}

	// Add symbols one-by-one.
	for (symbol = 0; symbol < code_lengths_size; ++symbol) {
	  if (code_lengths[symbol] > 0) {
		if (!TreeAddSymbol(tree, symbol, codes[symbol], code_lengths[symbol])) {
		  goto End;
		}
	  }
	}
	ok = 1;
 End:
	free(codes);
	ok = ok && IsFull(tree);
	if (!ok) HuffmanTreeRelease(tree);
	return ok;
  }
}

int HuffmanTreeBuildExplicit(HuffmanTree* const tree,
							 const int* const code_lengths,
							 const int* const codes,
							 const int* const symbols, int max_symbol,
							 int num_symbols) {
  int ok = 0;
  int i;

  assert(tree != NULL);
  assert(code_lengths != NULL);
  assert(codes != NULL);
  assert(symbols != NULL);

  // Initialize the tree. Will fail if num_symbols = 0.
  if (!TreeInit(tree, num_symbols)) return 0;

  // Add symbols one-by-one.
  for (i = 0; i < num_symbols; ++i) {
	if (codes[i] != NON_EXISTENT_SYMBOL) {
	  if (symbols[i] < 0 || symbols[i] >= max_symbol) {
		goto End;
	  }
	  if (!TreeAddSymbol(tree, symbols[i], codes[i], code_lengths[i])) {
		goto End;
	  }
	}
  }
  ok = 1;
 End:
  ok = ok && IsFull(tree);
  if (!ok) HuffmanTreeRelease(tree);
  return ok;
}

